class Main:    language_txt = None    start = True    def __init__(self, server, user):        self.server = server        self.user = user    def greet(self, user, language):        if language == 'ru':            Main.language_txt = 'ru'            print(f'Поздравляю! {user}, вы '                  f'зарегестрировались на почте NetSpace')        else:            Main.language_txt = 'en'            print(f'Congratulation! {user}, you '                  f'have registered with NetSpace ')    def language(self):        print('▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄')        print('Вы можете выбрать язык  |  You can choose language')        print('\t\t\t\tRussia  |  English')        language_text = input()        while language_text.lower() != 'russia' and \                language_text.lower() != 'english':            print('Вы ввели не тот язык попробуйте ещё | You entered'                  ' language please try again ')            language_text = input()        if language_text == 'russia':            return 'ru'        return 'en'class Servers(Main):    servers_birthday = []    def __init__(self, server, user):        super().__init__(server, user)        super().greet(user, super().language())        self.documentation_view = False        self.attempts = 3    def __call__(self, server):        if not self.documentation_view:            Documentation.documentation()            self.documentation_view = True        all_servers = ['125.038.018', '192.098.019', '100.000.111',                       '362.600.008',                       '200.469.893', '347.892.047']        if Main.start:            if server in all_servers:                self.attempts = 3                return True            if Servers.servers_birthday:                if server in Servers.servers_birthday[0]:                    print('WOW YOU HAVE BIRTHDAY!')                    return True            else:                self.attempts -= 1                if Main.language_txt == 'ru':                    if self.attempts == 2:                        print(f'Вы ввели не тот сервер ( ставшиеся попытки : '                              f'{self.attempts} ) ¯\_(⊙_ʖ⊙)_/¯')                    elif self.attempts == 1:                        print(f'Вы ввели не тот сервер ( ставшиеся попытки : '                              f'{self.attempts} ) ヽ(。_°)ノ')                    else:                        print('(╯°□°）╯︵ ┻━┻ ')                else:                    if self.attempts == 2:                        print(f'You enter wrong server ( remaining '                              f'attempts : {self.attempts} ) ¯\_(⊙_ʖ⊙)_/¯')                    elif self.attempts == 1:                        print(f'You enter wrong server '                              f'( remaining attempts : {self.attempts} ) ヽ(。_°)ノ')                    else:                        print('(╯°□°）╯︵ ┻━┻ ')                if self.attempts == 0:                    MailClient.exit()                return False        return Falseclass Documentation(Main):    if Main.start:        @classmethod        def documentation(cls):            print('-------------------------------------------------')            if Main.language_txt == 'en':                print('Rules!')                print('Our servers not for Minecraft, GTA, Google and etc.')                print('Using our mail you must: \n1.Observe the rules of etiquette'                      '\n 2. Do not spam emails from administrators(as this may lead to a ban)'                      '\n 3. do not promote any services prohibited for the programmer:'                      '\n\t 1.1. Fix Windows in 5 minutes, pensioners get a discount.'                      '\n\t 1.2. I will Teach you to keep all the'                      ' documentation in your head in just 3 years!'                      '\n\t 1.3. I will Teach you how to write <<hello word>> just$500')            else:                print('Правила!')                print('Наши сервера не для Майнкрафта, ГТА, Google и т.п.')                print(                    'Пользуясь нашей почтой вы должны: \n1.Соблюдать правила этикета'                    '\n2.Не спамить письмами администраторов'                    '(так как это может повлечь за собой бан)'                    '\n3.Не пропагандировать всякие запрещенные для программиста услуги:'                    '\n\t1.1.Починю винду за 5 минут, пенсионерам скидка.'                    '\n\t1.2.Научу держать вас всю документацию в голове всего за 3 года!'                    '\n\t1.3.Научу вас писать <<hello word>> за 500$')            print('-------------------------------------------------')            print('Вы согласны следовать нашим правилам? | Do you agree to the rules?\n(да/yes)')            user = 'нет'            while True:                if user.lower() == 'да' or user.lower() == 'yes':                    print('''Servers     |     Сервера125.038.018 | 192.098.019347.892.047 | 100.000.111200.469.893 | 362.600.008''')                    break                user = input()class MailClient(Servers):    def __init__(self, server, user):        super().__init__(server, user)    def receive_mail(self):        if Main.start:            print(f'Спасибо {self.user}, ваша почта получена ( ͡° ͜ʖ ͡°)')        return    def send_mail(self, server1, user1, message):        if Main.start:            if super().__call__(server1):                print(f'Письмо\n{message} :: отправлено {user1}')                return    def happy_birthday(self):        if Main.start:            Birthday.add_servers()    @classmethod    def exit(cls):        print('©Created by Arkadiy ฅ^•ﻌ•^ฅ')        print('▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄')        Main.start = Falseclass Birthday(Servers):    """этот класс дает дополнительные(блатные) сервера в честь дня рождения"""    @classmethod    def add_servers(cls):        print('''!Happy Birthday with NetSpace!\t  [̲̅$̲̅(̲̅ ͡° ͜ʖ ͡°̲̅)̲̅$̲̅]000.000.000 | 999.999.999222.222.222 | 333.333.333111.111.111 | 888.888.888\t  [̲̅$̲̅(̲̅ ͡° ͜ʖ ͡°̲̅)̲̅$̲̅]!Happy Birthday with NetSpace!''')        Servers.servers_birthday.append(['000.000.000', '888.888.888', '333.333.333', '111.111.111',                                         '222.222.222', '999.999.999'])# пример вызова# a = MailClient('1.294.102', 'ПЕТРР')# a.send_mail('200.409.893', 'Вова', 'Привет друг')# a.send_mail('11', 'gh', '3')# a.happy_birthday()# a.send_mail('000.000.000', 'Вова', 'Привет друг')